@create_ui
lib_ui output_handle=main_window_handle method=create type=window title=main_window_title x=100 y=100 width=200 height=270 style=WS_CAPTION|WS_SYSMENU|WS_VISIBLE extrastyle=WS_EX_WINDOWEDGE|WS_EX_TOPMOST
lib_ui method=window.settray handle={main_window_handle} photo={photo}
lib_ui method=window.seticon handle={main_window_handle} photo={photo}
lib_ui method=menu.appenditem handle={main_window_handle} title=1 id=1
end

@setEnv
set true true
set false false
set click 1
set photo 
end

@blind_events
lib_ui method=blindevent handle={main_window_handle} blindevent=trayevent blindmethod=tray_event
lib_ui method=blindevent handle={main_window_handle} blindevent=onuserclose blindmethod=on_user_close
lib_ui method=blindevent handle={main_window_handle} blindevent=onmenuclick id=1 blindmethod=onmenuclick
end
@onmenuclick
echo 1
end

@tray_event
if {main_window_handle}|eventtype|trayevent==click lib_ui method=menu.popupmenu handle={main_window_handle} style=TPM_BOTTOMALIGN|TPM_RETURNCMD
end

@on_user_close
echo user close{endl}
lib_ui method=exit
end

@main
call setEnv
call create_ui
call blind_events
lib_ui method=run
end


call main